mapper.select:
select c.id, c.user_id, c.chapter_id, c.content, c.like_cnt, c.is_spoiler, c.create_time, c.update_time from comment c where c.chapter_id in
#{chapterId}
order by c.create_time desc

select

from comment


        and chapter_id = #{chapterId,jdbcType=INTEGER}


        and user_id = #{userId,jdbcType=OTHER}


        and is_spoiler = #{isSpoiler,jdbcType=BIT}


        and content like concat('%', #{search}, '%')




        order by like_cnt ${order}


        order by create_time ${order}


limit #{size} offset #{page} * #{size}

select count(*)
from comment


        and chapter_id = #{chapterId,jdbcType=INTEGER}


        and user_id = #{userId,jdbcType=OTHER}


        and is_spoiler = #{isSpoiler,jdbcType=BIT}


        and content like concat('%', #{search}, '%')

select count(*)
from comment
where chapter_id = #{chapterId,jdbcType=INTEGER}

select count(*)
from comment c
where c.chapter_id in

    #{chapterId}


    and c.is_spoiler = #{isSpoiler,jdbcType=BIT}


    and c.content like concat('%', #{search}, '%')

mapper.update:
update comment
set like_cnt = like_cnt + #{increment,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select count(*) > 0
from comment
where user_id = #{userId,jdbcType=OTHER} and chapter_id = #{chapterId,jdbcType=INTEGER}

SELECT COUNT(*)
FROM comment
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM comment
WHERE id IN

    #{id}

mapper.select:
SELECT *
FROM comment
WHERE like_cnt >= #{minLikes}
ORDER BY like_cnt DESC, create_time DESC
LIMIT #{limit}
